function binarySearch(array, target) {
    let leftmost = 0;
    let righmost = array.length - 1;
    let splitCounter = 1;

    while (leftmost <= righmost) {
        const middle = Math.floor((leftmost + righmost) / 2);

        if (array[middle] < target) {
            leftmost = middle + 1;
        }
        else if (array[middle] > target) {
            righmost = middle - 1;
        }
        else {
            return [splitCounter, middle];
        }
        //this is our way of getting the number of splits needed
        splitCounter++;
    }
    return [-1, 0];
}

//making an array of 1024 numbers
let array = [];
for (let i = 1; i <= 1024; i++) {
    array.push(i);
}
console.log(array);

//for 100 trials 
let datasets = [];
let labels = [];
let numberOfTrials = 10

for (let i = 1; i <= numberOfTrials; i++) {
    let target = Math.ceil(Math.random() * 1024);

    //getting the time 
    const startTime = new Date(); // capture the start time

    // code to be timed
    binarySearch(array, target)

    const endTime = new Date(); // capture the end time

    const elapsedTime = endTime.getTime() - startTime.getTime();// calculate the difference in milliseconds
    // console.log("Elapsed time:", elapsedTime, "ms");

    //running up the function
    let splits = binarySearch(array, target);

    //pushing only the number of splits
    const [numberOfSplits, middle] = splits
    datasets.push(numberOfSplits)

    labels.push(target)
}

